import com.eviware.soapui.model.testsuite.TestStepResult.TestStepStatus
import groovy.json.JsonSlurper
import com.eviware.soapui.support.XmlHolder
import net.sf.*;
import net.sf.json.*;
import net.sf.json.groovy.*;



def responseobj= context.expand( '${Get Demographics search exact match#Response#$.[*].id}' )
def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)
def ResponseMessage = testRunner.testCase.testSteps["Get Demographics search exact match"].testRequest.response.contentAsString
def jsonSlurper = new JsonSlurper().parseText(ResponseMessage)
assert jsonSlurper!=null

def firstnames= jsonSlurper.firstName
assert firstnames!=null

def lastnames= jsonSlurper.lastName
assert lastnames!=null

def genders= jsonSlurper.gender
assert genders!=null

def zipCodes= jsonSlurper.zipCode
assert zipCodes!=null

def birthDays= jsonSlurper.birthDay
assert birthDays!=null

def confidenceScores= jsonSlurper.confidenceScore
assert confidenceScores!=null


def firstName = context.expand( '${DataSource#firstName}' )
def lastName = context.expand( '${DataSource#lastName}' )
def zipCode = context.expand( '${DataSource#zipCode}' )
def gender = context.expand( '${DataSource#gender}' )
def birthDay = context.expand( '${DataSource#birthDay}' )


for(int i=0; i<firstnames.size();i++){
	assert firstnames[i].equals(firstName)  ||  firstnames[i].equals (lastName)
}

for(int j=0; j<lastnames.size();j++){
	assert lastnames[j].equals(firstName)  || lastnames[j].equals (lastName)
}

for(int k=0; k<zipCodes.size();k++){
	assert zipCodes[k].equals(zipCode)
}

for(int l=0; l<genders.size();l++){
	assert genders[l].equals(gender)
}

for(int m=0; m<birthDays.size();m++){
	assert birthDays[m].equals(birthDay)
}

for(int n=0; n<confidenceScores.size();n++){
	assert confidenceScores[n]==100
}

