import javax.xml.xpath.*
import javax.xml.parsers.DocumentBuilderFactory
import groovy.json.JsonSlurper

def gwLocationResponse = context.expand( '${getGwLocations#ResponseAsXml#declare namespace ns1=\'https://api-test.greenwayhealth.com/Integration/RESTv1.0/PrimeSuiteAPIService/ScheduleLocationsGet\'; //ns1:Response[1]}' )
def msLocationResponse = context.expand( '${getMsGwLocations#ResponseAsXml#//Response[1]}' )

def gwResponse = new XmlSlurper().parseText(gwLocationResponse)
def gwLocationIDList = gwResponse.depthFirst().findAll { it.name() == 'LocationID'}
def gwLocationNameList = gwResponse.depthFirst().findAll { it.name() == 'Name'}

def msResponse = new XmlSlurper().parseText(msLocationResponse)
def msLocationIDList = msResponse.depthFirst().findAll { it.name() == 'id'}
def msLocationNameList = msResponse.depthFirst().findAll { it.name() == 'name'}

for(gwId in gwLocationIDList)
{
	for(msId in msLocationIDList)
	{
		if(gwId==msId)
		{

				log.info "asserting gw $gwId with ms $msId"
				assert gwId==msId
		}

	}
}
log.info "locations List $gwLocationNameList and $msLocationNameList"

for(gwName in gwLocationNameList)
{
	for(msName in msLocationNameList)
	{
		if(gwName==msName)
		{

				log.info "asserting gw $gwName with ms $msName"
				assert gwName==msName
		}

	}
}
log.info "asserting locationId count"
//assert gwLocationIDList.size()==msLocationIDList.size()
log.info "asserting locationName count"
//assert gwLocationNameList.size()==msLocationNameList.size()

