import groovy.json.JsonSlurper
import groovy.json.JsonBuilder

def response = context.expand( '${getPssServiceLocations#Response}' )
def jsonObj = new JsonSlurper().parseText( response )
int index

for(int i=0;i< jsonObj.size() ; i++) {
 if(!jsonObj[i].selected ) {  	
  index = i
 }
}
def save = [ 
    [ getDisplayName : { jsonObj[index].displayName }, getExtLocationId : { jsonObj[index].extLocationId }, getName : { jsonObj[index].name }, getDesc : { jsonObj[index].description }, getAddress : { jsonObj[index].address }, getId : { null } ], 
]
def jsonBuilder = new groovy.json.JsonBuilder()
jsonBuilder(
    save.collect { 
        [ 
        	  id: it.getId(),
            name: it.getName(),
            displayName: it.getDisplayName(), 
            description: it.getDesc(), 
            extLocationId: it.getExtLocationId(), 
            address : it.getAddress()
        ]
    }
)
jsonBuilder.toPrettyString()

import JsonNodeBuilders.*
import groovy.json.JsonSlurper

def pssServiceResponse = context.expand( '${getAssociatedLocation#ResponseAsXml}' )
def pssResponse = new XmlSlurper().parseText(pssServiceResponse)
def pssLocationSortList = pssResponse.depthFirst().findAll { it.name() == 'sortOrder'}
log.info pssLocationSortList.size()

for(int x = 0; x < pssLocationSortList.size()-1; x++)
{
	//log.info pssLocationSortList.size()
 int firstValue=Integer.parseInt(pssLocationSortList.getAt(x).toString())
 int secondValue=Integer.parseInt(pssLocationSortList.getAt(x+1).toString())
 isSorted = firstValue<secondValue

 if(!isSorted) { 
  isSorted=false
  log.info "list is sorted"
  break 
 }
}
assert isSorted
log.info "list is sorted"

import groovy.json.JsonBuilder
import JsonNodeBuilders.*
import groovy.json.JsonSlurper
import java.util.Random  

def pssServiceResponse = context.expand( '${getAssociatedLocation#ResponseAsXml}' )
def pssResponse = new XmlSlurper().parseText(pssServiceResponse)
def pssLocationSortList = pssResponse.depthFirst().findAll { it.name() == 'sortOrder'}
sortlistsize=pssLocationSortList.size()-1
def source= Math.abs(new Random().nextInt() % sortlistsize + 1)

def target= Math.abs(new Random().nextInt() % sortlistsize + 1)
if(source==target) {
	target
}
if(source!=target)
{
	def save = [ 
    [ getsource : { source }, gettarget : { target } ],
]
def jsonBuilder = new groovy.json.JsonBuilder()
jsonBuilder(
    save.collect { 
        [ 
            source: it.getsource(),
            target: it.gettarget(), 

        ]
    }
)
jsonBuilder.toPrettyString()

def reorderjson= jsonBuilder.toPrettyString();
def reorder= (reorderjson.substring(1, reorderjson.length()-1));
}
else
{
def save = [ 
    [ getsource : { source }, gettarget : { target } ],
]
def jsonBuilder = new groovy.json.JsonBuilder()
jsonBuilder(
    save.collect { 
        [ 
            source: it.getsource(),
            target: it.gettarget(), 
        ]
    }
)
jsonBuilder.toPrettyString()
def reorderjson= jsonBuilder.toPrettyString();
def reorder= (reorderjson.substring(1, reorderjson.length()-1));	
}

