con:config.script:
def headerValue = testRunner.testCase.getTestStepByName("postBalanceFromRsdk").httpRequest.response.responseHeaders["Location"]
context.testCase.setPropertyValue("Value",headerValue.toString())
String test=context.testRunner.testCase.getPropertyValue("Value");
test = test.replace("[","").replace("]","");

String componentName = testRunner.testCase.getTestStepByName("postBalanceFromRsdk").httpRequest.response.responseHeaders["IHG_LC_ComponentName"]
//assert componentName=="[Secure Payment Message Data Channel]"
log.info componentName
//HTTP GET 
log.info "Get Processing URL...."
HttpURLConnection conn = (HttpURLConnection) test.toURL().openConnection()
conn.setRequestProperty( "Content-Type", "application/xml" )
def accessToken = context.expand( '${#Project#accessToken}' )
conn.setRequestProperty( "Authorization", accessToken )
if( conn.responseCode == 200 ) {
	def json = conn.getInputStream().getText()
	String jsonText = json.toString()
	log.info jsonText

	 def processingURL = new XmlSlurper().parseText(jsonText)
	 def statusCheck = processingURL.depthFirst().findAll { it.name() == 'State'}
	 log.info statusCheck
	 for(status in statusCheck) {
	 	assert status == "COMPLETED"
	 }
} else {
  log.info "Error while processing URL."
  log.info "${conn.responseCode}: ${conn.responseMessage}" 
}

def headerValue = testRunner.testCase.getTestStepByName("invalidPatientExternalId").httpRequest.response.responseHeaders["Location"]
context.testCase.setPropertyValue("Value",headerValue.toString())
String test=context.testRunner.testCase.getPropertyValue("Value");
test = test.replace("[","").replace("]","");

//HTTP GET 
log.info "Get Processing URL...."
HttpURLConnection conn = (HttpURLConnection) test.toURL().openConnection()
conn.setRequestProperty( "Content-Type", "application/xml" )
def accessToken = context.expand( '${#Project#accessToken}' )
conn.setRequestProperty( "Authorization", accessToken )
if( conn.responseCode == 200 ) {
	def json = conn.getInputStream().getText()
	String jsonText = json.toString()
	log.info jsonText

	 def processingURL = new XmlSlurper().parseText(jsonText)
	 def statusCheck = processingURL.depthFirst().findAll { it.name() == 'State'}
	 String errorCode = processingURL.depthFirst().findAll { it.name() == 'Code'}
	 String description = processingURL.depthFirst().findAll { it.name() == 'Description'}
	 log.info statusCheck
	 for(status in statusCheck) {
	 	if(status == "ERROR") {
	 		log.info "Error Code= $errorCode"
	 		log.info "Description= $description"
	 		assert description.contains('1000000100010001001')	
	 	}
	 }
} else {
  log.info "Error while processing URL."
  log.info "${conn.responseCode}: ${conn.responseMessage}" 
}

