def holder = new XmlHolder( messageExchange.responseContentAsXml ) holder.namespaces["ns3"] = "http://schema.intuit.com/health/securePayments/v1" def node = holder.getDomNode( "//ns3:ProcessingResponse[1]/ResponseMessage[1]" )
String description = context.expand( '${invalidDateFormatInPayload#Response#declare namespace ns3=\'http://schema.intuit.com/health/securePayments/v1\'; //ns3:ProcessingResponse[1]/ResponseMessage[1]}' ) String balanceDate = context.expand( '${generatePayloadRandomData#balanceDate}' ) log.info description log.info balanceDate assert !description.contains(balanceDate) assert node != null

